import { addItem,removeItem,toggleItem } from "./action";

export const reducer=(store,{type,payload})=>{
    switch(type){
        case addItem:
            return {todos:[...store.todos,payload]}
        case toggleItem:
            return {todos:store.todos.map((e)=>(
                e.id==payload ? {...todo,status:!todo.status} :todo
            ))}
        case removeItem:
            return {
                todos:store.todos.filter((e)=>(
                    e.id!=payload
                ))
            }
        default:
        return store;
    }
}



//todo=[{id,work,status,}]
