export const Add_Item='Add_Item';
export const Toggle_Item="Toggle_Item";
export const Remove_Item="Remove_Item";

export const addItem=(action)=>({
    type:Add_Item,
    payload:action
})
export const toggleItem=(action)=>({
    type:Toggle_Item,
    payload:action
})
export const removeItem=(action)=>({
    type:Remove_Item,
    payload:action
})
