import { GET_ALL_LATEST_TODOS_SUCCESS, GET_ALL_LATEST_TODOS_FAILURE, GET_ALL_LATEST_TODOS_LOADING} from "./action"

export const reducer=(store,action)=>{
    switch(action.type){
        case GET_ALL_LATEST_TODOS_LOADING:
            return {todos:[],isLoading:true,isError:false};
        case GET_ALL_LATEST_TODOS_FAILURE:
            return {todos:[],isLoading:false,isError:true};
        case GET_ALL_LATEST_TODOS_SUCCESS:
            return {todos:[...payload],isLoading:false,isError:false};
        case Default:
            return store;
    }
}
