import { addItem,removeItem,toggleItem } from "./action";

export const reducer=(store,action)=>{
    switch(type){
        case action.type==addItem:
            return {todos:[...store.todos,action.payload]}
        case toggleItem:
            return {todos:store.todos.map((e)=>(
                e.id==action.payload ? {...e,status:!e.status} :e
            ))}
        case removeItem:
            return {
                todos:store.todos.filter((e)=>(
                    e.id!=action.payload
                ))
            }
        default:
        return store;
    }
}



//todo=[{id,work,status,}]
