export const Add_Item='Add_Item';
export const Toggle_Item="Toggle_Item";
export const Remove_Item="Remove_Item";

export const addItem=(payload)=>({
    type:Add_Item,
    payload
})
export const toggleItem=(payload)=>({
    type:Toggle_Item,
    payload
})
export const removeItem=(payload)=>({
    type:Remove_Item,
    payload
})
